set
  realname = #{realname,jdbcType=VARCHAR},
  cardnum = #{cardnum,jdbcType=VARCHAR},
  tel = #{tel,jdbcType=CHAR}
where id = #{id,jdbcType=INTEGER}

update t_member
set
  email = #{email}
where id = #{id}

update t_member
set
  authstatus = #{authstatus}
where id = #{id}
